mapper.select:
select grouplist_name
from bumoim.grouplist
where grouplist_name = #{grouplist_name}

SELECT member_id, member_name, member_group1, member_group2, member_group3
FROM member
WHERE member_id = #{member_id}

SELECT member_id, member_number, member_name, member_major
FROM member
WHERE member_group1 = #{group_number} || member_group2 = #{group_number} || member_group3 = #{group_number}
